select 'strat_id',  'trade_id', 'instrument', 'buy_date', 'sell_date', 'enterCRSI', 'buy_price', 'sell_price', 'returns', 'holding_days'  union all select a.strat_id, a.trade_id, a.instrument, FROM_UNIXTIME(a.buy_date), FROM_UNIXTIME(a.sell_date), a.buy_price, a.sell_price , a.returns, a.holding_days, a.entry_1, a.entry_2, a.entry_3, a.entry_4 from simResultAllTrades a, simInputs b where a.run_id = 12 and b.run_id = 11 INTO OUTFILE '/StockValuation/algo/output/simAllTrades.csv' FIELDS TERMINATED BY ',' ENCLOSED BY '"' LINES TERMINATED BY '\n'
